import './App.css';
import { Component } from 'react';

import Console from './console';


class App extends Component{
  state={
    courseid:547,
    coursename:"rudra",
    package:20000,
    courseval:flase
  }
   mycoursdata=()=>{

    let coursevalue=this.state.courseval;
    this.setState({
      courseval:!coursevalue,
    })
  }

  render(){
    let data=null
    if(this.state.courseval=true){
      data=(<p>this.state.courseid</p>)
    }
  return (

    <div>
      <button onClick={this.mycoursdata}>Submit</button>
      {data}
    <Console mypack={this.state.package}/>
    </div>
  );}
}

export default App;
