import React,{useContext} from 'react'
import { shopdata } from './Context'
import {Link} from 'react-router-dom'
function Home() {
  const { data,addToCart } = useContext(shopdata)
  return (
      <div className="row" style={{padding:"20px",position:"relative"}}>
        <Link to="/cart" >cart</Link>
          {
              data.map((value) => {
                console.log(value)
                  return (
                      <div key={value.id} className="card w-33" style={{width: "18rem",padding:"20px",position:"relative",outline:"none",border:"0px"}}>
                      <img className="card-img-top" src={value.image} alt="Card image cap"></img>
                      <div className="card-body">
                        <p className="card-text">price : {value.cost} $</p>
                        <button className='btn btn-outline-dark' onClick={()=>addToCart(value)}>Add to cart</button>
                      </div>
                    </div>
                  )
              })
          }
      </div>
  )
}

export default Home
