import './App.css';
import { Component } from 'react';




class App extends Component{
  state={
    courseId:547,
    coursename:"react",
    courses:["js","java","turbo"],
    info:null,
  }

  showCourseInfo=()=>{
    console.log("hello")
    this.setState()
    this.state.info=(
      <div>

        <p>{this.state.courseId}</p>
        <p>{this.state.coursename}</p>
      </div>
    )
  }

  render(){

    return(
     <div>
      <button onClick={this.showCourseInfo}>display</button>
      {this.state.info}
     </div>
    )
  }
}
export default App;
