import './App.css';
import { Component } from 'react';




class App extends Component{
  state={
    courseId:547,
    coursename:"react",
    courses:["js","java","turbo"],
  }

  render(){
    let course=this.state.courses.map(value=>{
      <div>
        <p>{value}</p>
      </div>
    })
    return(
     <div>
      <button >display</button>
      {course}
     </div>
    )
  }
}
export default App;
