import React, { Component } from 'react'
const StudentInfoContext=React.createContext();
const myreducer=(state,action)=>{
  console.log("clicked")
}
export class StudentInfo extends Component {
    state={
        id:"rudra",course:"react",age:20,package1:20000,
        dispatch:(action)=>{
          this.setState( state => myreducer(this.state,action))
        }
    }
  render() {
    return (
      <StudentInfoContext.Provider value={this.state}>
        {this.props.children}
      </StudentInfoContext.Provider>
    )
  }
}
export const StudentConsumer=StudentInfoContext.Consumer;
