import './App.css';
import { Component } from 'react';




class App extends Component{
  state={
    courseId:"c-99",
    courseName:"react",
    cost:25000,
    showCourse:false,
  }
  showCourseInfo=()=>{
    let val=this.state.showCourse;
    this.setState({
      showCourse:!val,
    })
  }
  render(){
    let mycourseinfo=null;
    if(this.state.showCourse===true){
      mycourseinfo=(<div>
      <p>{this.state.courseId}</p>
      <p>{this.state.courseName}</p>
      <p>{this.state.cost}</p>
      </div>)
    }
    return(
      <div>
        <button onClick={this.showCourseInfo}>display</button>
        {mycourseinfo}
      </div>
    )
  }
}
export default App;
