import './App.css';
import { Component } from 'react';
import './base.css'

class App extends Component{
  state={
    courseId:547,
    coursename:"react",
    courses:["js","java","turbo"],
    info:null,
  }

  showCourseInfo=()=>{
    console.log("hello")
    this.setState({
     info:(
      <div>

        <p>{this.state.courseId}</p>
        <p>{this.state.coursename}</p>
      </div>
     )
  });
}

  render(){
   let courseval=this.state.courses.map(val=>{
    return(
      <div>
        <p>{val}</p>
      </div>
    )
   })
    return(
     <div>
      <button onClick={this.showCourseInfo} className="myclass">display</button>
      {this.state.info}
      {courseval}
     </div>
    )
  }
}
export default App;
