import {Component} from 'react';

class course extends Component{
    state={
        courseId:"c-12",
        courseName:"React 16",
        cost:15000,
        trainer:"srinivas dande",
        showStatus:false,
    }

    render(){
        let data
        const showData=()=>{
            let val=this.state.showStatus;
            this.setState({
            showStatus:!val,
            })
            console.log(this.state.showStatus)
            data=null;
            if(this.state.showStatus===true){
                data=( <div>
                        <p>{this.state.courseId}</p>
                        <p>{this.state.courseName}</p>
                        <p>{this.state.cost}</p>
                    </div>
                )
            }
        }
        console.log("course component")
            return(
                <div>
                    <button onClick={showData}>Course Info</button>
                    
                    {data}
                </div>
            )
    }
}
export default course;
