import './App.css';
import { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
class App extends Component{

  state={
    student1:[
      101,"react",15000,
    ],
    student2:{
      courseId:102,coursename:"react",fee:15000,
    },
    student3:{
      courseId:103,coursename:"react",fee:15000,
    }
  }


  render(){
   let showDetailInfo=(val)=>{
      console.log(val);
      let data=this.state.filter((course)=>course.courseId===val)
      console.log(data)
     }
    return(
      <div>
        <table className='table table-dark table-stripped'>
        <thead>
          <tr>
            <th scope='col'>Id</th>
            <th scope='col'>name</th>
            <th scope='col'>fee</th>
            <th></th>
          </tr>
          <tr scope='row'>
            <td>{this.state.student1[0]}</td>
            <td>{this.state.student1.coursename}</td>
            <td>{this.state.student1.fee}</td>
            <td><button className='btn btn-light' onClick={showDetailInfo.bind(this,this.state.student1.courseId)}>More</button></td>
          </tr>
          <tr scope='row'>
            <td>{this.state.student2.courseId}</td>
            <td>{this.state.student2.coursename}</td>
            <td>{this.state.student2.fee}</td>
            <td><button className='btn btn-primary'>More</button></td>
          </tr>
          <tr scope='row'>
            <td>{this.state.student3.courseId}</td>
            <td>{this.state.student3.coursename}</td>
            <td>{this.state.student3.fee}</td>
            <td><button className='btn btn-success'>More</button></td>
          </tr>
        </thead>
        </table>
      </div>

    )
    }
    }
export default App;
