import React,{useContext} from 'react'
import { shopdata } from './Context'
function Home() {
  const { data,addToCart } = useContext(shopdata)
  return (
      <div className="row" style={{padding:"20px",position:"relative"}}>
          {
              data.map((value) => {
                  return (
                      <div className="card w-33" style={{width: "18rem",padding:"20px",position:"relative",outline:"none",border:"0px"}}>
                      <img className="card-img-top" src={value.image} alt="Card image cap"></img>
                      <div className="card-body">
                        <p className="card-text">price : {value.cost} $</p>
                        <button className='btn btn-outline-dark' onClick={()=>addToCart(value.id)}>Add to cart</button>
                      </div>
                    </div>
                  )
              })
          }
      </div>
  )
}

export default Home
