import React, { useContext } from 'react'
import { shopdata } from './Context'

export default function Cart() {
    const { cart } = useContext(shopdata)
    return (
        <div>
           {
            cart.map(value=>{
                return(
                    <div key={value.id} className='card w-25'>
                        <img className="card-img-top" src={value.image} alt="Card image cap"></img>
                      <div className="card-body">
                        <p className="card-text">price : {value.cost} $</p>
                      </div>
                    </div>
                )
            })
           }
        </div>
    )
}
