import {Component} from 'react';

class course extends Component{
    state={
        courseId:"c-12",
        courseName:"React 16",
        cost:15000,
        trainer:"srinivas dande",
        showStatus:false,
    }
    showData=()=>{
       let val=this.state.showStatus;
        this.setState({
        showStatus:!val,
        })
    }

    render(){
        let data
      
         
            console.log(this.state.showStatus)
            data=null;
            if(this.state.showStatus===true){
                data=( <div>
                        <p>{this.state.courseId}</p>
                        <p>{this.state.courseName}</p>
                        <p>{this.state.cost}</p>
                    </div>
                )
            }
        
       
            return(
                <div>
                    <button onClick={this.showData}>Course Info</button>
                    
                    {data}
                </div>
            )
    }
}
export default course;
