import React, { useState } from 'react'
import shop1 from './Images/shop1.png'
import shop2 from './Images/shop2.png'
import shop3 from './Images/shop3.png'
import shop4 from './Images/shop4.png'
import shop5 from './Images/shop5.png'
import shop6 from './Images/shop6.webp'
import { useContext } from 'react'
export const shopdata = React.createContext()
export function useProduct(){
  return useContext(shopdata)
}
const data =
  [
    {
      id: 1,
      name: "iphone",
      cost: 999,
      image: shop1
    },
    {
      id: 2,
      name: "camera",
      cost: 699,
      image: shop2
    },
    {
      id: 3,
      name: "camera",
      cost: 8999,
      image: shop3
    },
    {
      id: 4,
      name: "camera",
      cost: 99,
      image: shop4
    },
    {
      id: 5,
      name: "shirt",
      cost: 999,
      image: shop5
    },
    {
      id: 6,
      name: "iphone",
      cost: 89,
      image: shop6
    },
  ]

export function Context({ children }) {
  const [cart, setcart] = useState([])
  function cartItems(){
    return cart
  }
  function addToCart(id) {
    const arr = data.filter(value => value.id === id)
    setcart([...cart, ...arr])
    console.log(cart)
  }
  const totaldata = {
    data,
    addToCart,
    cart,
    cartItems
  }
  return (
    <shopdata.Provider value={totaldata}>
      {children}
    </shopdata.Provider>
  )
}
