import {Component} from 'react';
import { StudentConsumer } from './StudentInfo';

class Course extends Component{
    showMore=(value1,dispatch)=>{
        dispatch({
            type:"Update",
            payload:value1
        })
    }
    render(){
        return(
            <StudentConsumer>
                {
                    value=>{
                        return(
                            <div>
                                <center>
                                <h1>Student Info</h1>
                                <br>
                                </br>
                                <hr>
                                </hr>
                                <h3>{value.id}</h3>
                                <br></br>
                                <h3>{value.course}</h3>
                                <br></br>
                                <h3>{value.age}</h3>
                                <br></br>
                                <h3>{value.package1}</h3>
                                <br></br>
                                <br></br>
                                <button onClick={this.showMore.bind(this,20,value.dispatch)}>Display</button>
                                </center>
                            </div>
                        )
                    }
                }
            </StudentConsumer>
        )
    }
}
export default Course;
